import { Fragment, useState } from "react";
import card from "../../card";
import "./Filter.scss";

function Filter() {
    const [memoryValue, setMemoryValue] = useState();
    const [companyValue, setCompanyValue] = useState();
    const [cardItem, setcardItem] = useState(card)
    
    const filterCard = (event) => {
        event.preventDefault();
        if (memoryValue === "Memory" && companyValue === "Company") setcardItem(card)
        if (memoryValue !== "Memory")
        cardItem.filter((el) => el.memoryCapacity <= memoryValue && el.memoryCapacity === companyValue)

    }

    return (
        <Fragment>
            <form>
                <div>
                    <select onChange={(event) => {setMemoryValue(event.preventDefault())}}>
                        <option value={"Memory"}>Memory</option>
                        <option value={32}>To 32 Gb</option>
                        <option value={64}>From 32 to 64</option>
                        <option value={128}>From 64 to 128</option>
                        <option value={"2048"}>From 128 Gb...</option>
                    </select>
                    <select onChange={(event) => {setCompanyValue(event.preventDefault())}}>
                        <option value={"Company"}>Company</option>
                        <option value={"Sony"}>Sony</option>
                        <option value={"IoT"}>IoT</option>
                        <option value={"IgorAndNastya"}>IgorAndNastya</option>
                        <option value={"Other"}>Other</option>
                    </select>
                </div>
                <button type="submit" onClick={filterCard}>Apply</button>
            </form>
        </Fragment>
    )
}

export default Filter;
