import { useEffect } from "react";
import { Fragment, useState } from "react";
import card from "../../card";
import "./Filter.scss";

function Filter() {
    const [selectValue, setSelectValue] = useState();
    const [cardItem, setcardItem] = useState(card)
    const filterCard = (event) => {
        event.preventDefault();
        if (selectValue === "Memory") set
        cardItem.filter((el) => el <= selectValue)
    }

    return (
        <Fragment>
            <form>
                <div>
                    <select onChange={(event) => {setSelectValue(event.preventDefault())}}>
                        <option>Memory</option>
                        <option value={32}>To 32 Gb</option>
                        <option value={64}>From 32 to 64</option>
                        <option value={128}>From 64 to 128</option>
                        <option value={"more"}>From 128 Gb...</option>
                    </select>
                    <select>
                        <option>Company</option>
                        <option>Sony</option>
                        <option>IoT</option>
                        <option>IgorAndNastya</option>
                        <option>Other</option>
                    </select>
                </div>
                <button type="submit" onClick={filterCard}>Apply</button>
            </form>
        </Fragment>
    )
}

export default Filter;
